// Static values
const n8nCostPerMonth = 50;
const zapierCostPerTask = 0.025;
const daysInMonth = 30;

// Retrieve form inputs
const item = $input.item;
const scenario = $input.first().json.Scenario || "Default scenario";
const emailsPerDay = Number($input.first().json['Emails/day']) || 0;
const tasksPerEmail = Number($input.first().json['Tasks/email']) || 0;

// Calculations
const tasksPerDay = emailsPerDay * tasksPerEmail;
const tasksPerMonth = tasksPerDay * daysInMonth;
const zapierCostPerMonth = tasksPerMonth * zapierCostPerTask;
const savingsPerMonth = n8nCostPerMonth - zapierCostPerMonth;

// Output formatted table-like data
return [
  {
    json: {
      "Scenario": scenario,
      "Emails/day": emailsPerDay,
      "Tasks/email": tasksPerEmail,
      "Tasks/day": tasksPerDay,
      "Tasks/month": tasksPerMonth,
      "Zapier cost/month": `$${zapierCostPerMonth.toFixed(2)}`,
      "n8n cost/month": `$${n8nCostPerMonth.toFixed(2)}`,
      "Savings/month": `$${savingsPerMonth.toFixed(2)}`
    }
  }
];
